<?phpnamespace app\Models;class CityModel extends model{    private $table='city';    public function get_all()    {        $sql = "select * from $this->table ";        return self::do_select($sql);    }    public function get_single($id)    {        $sql = "select * from $this->table where province_id = ?";        return self::do_select($sql ,[$id] , true);    }    public function add(array $data)    {        $sql = "INSERT INTO $this->table (province_id, name) VALUES (?, ?)";        return self::do_query($sql , [$data['province_id'],$data['name']]);    }    public function update($name ,  $city_id)    {        $sql = "update $this->table set name = ? where id = ?";        return self::do_query($sql , [ $name , $city_id]);    }    public function delete($city_id)    {        $sql = "delete from $this->table where id = ?";         return self::do_query($sql , [$city_id]);    }}