<?phpnamespace app\Models;class UserModel extends model{    private $table='users';    public function get_all()    {        $sql = "select * from $this->table ";        return self::do_select($sql);    }    public function get_single($email)    {        $sql = "select * from $this->table where email = ?";        return self::do_select($sql ,[$email] , 'fetch');    }    public function get_single_by_id($id)    {        $sql = "select * from $this->table where id = ?";        return self::do_select($sql ,[$id] , false);    }    public function add(array $data)    {        $sql = "INSERT INTO $this->table (email, name , permission) VALUES (?,?,?)";         return self::do_query($sql , [$data['email'] ,$data['name'] ,$data['permission']]);    }    public function update($email , array $data)    {        $sql = "update $this->table set name = ? , email = ? , permission = ?             where email = ?";        return self::do_query($sql , [$data['name'] , $data['email'] , $data['permission'], $email]);    }    public function delete($email)    {        $sql = "delete from $this->table where email = ?";         return self::do_query($sql , $email);    }}