<?phpnamespace app\Repositories;use app\Models\UserModel;class UserRepository{    public $model;    public function __construct()    {        $this->model = new UserModel();    }    public function getAll()    {        return $this->model->get_all();    }    public function getSingle($email)    {        return $this->model->get_single($email);    }    public function getSingleById($id)    {        return $this->model->get_single_by_id($id);    }    public function add($data)    {        return $this->model->add($data);    }    public function update($email , $data)    {        return $this->model->update($email , $data);    }    public function delete($email)    {        return $this->model->delete($email);    }}