<?phpnamespace app\Services;use Firebase\JWT\JWT;use Firebase\JWT\Key;class JWTService{    private const KEY = '7learn';    private const ALG = 'HS256';    public static function createToken($user_id)    {        $payload = ['user_id' => $user_id];        return JWT::encode($payload , self::KEY , self::ALG);    }    public static function decodeToken($jwt)    {        return JWT::decode($jwt , new Key(self::KEY , self::ALG), array(self::ALG));    }}