<?phpnamespace app\Validations;class Validation{    const int = 'is_int';    const numeric = 'is_numeric';    const string = 'is_string';    const array = 'is_array';    const set = 'isset';    private static bool $result = true;    public static function validate($value , array $validations=[])    {        $response = true;        foreach ($validations as $key=>$parameter) {                $response = $response & $validations[$key]($value);        }        return $response;    }}